import React, { useState, useEffect } from "react";
import { getOneByTitle } from "../hooks/hooks";

export const Movie = (props) => {
  const param = props.match.params.title;
  const [movie, setMovie] = useState({});
  const [favs, setFavs] = useState([]);

  useEffect(() => {
    getOneByTitle(param).then((data) => setMovie(data));
  }, []);

  return (
    <>
      <nav>
        <button onClick={() => setFavs([...favs, movie])}>
          {console.log(favs)}
          Agregar a Favoritos
        </button>
      </nav>
      <h1>{movie.Title}</h1>
      <br />
      <img src={movie.Poster} alt={movie.Title} />
      <br />
      <h2>Description</h2>
      {movie.Plot}
      <h2>Year and Genre</h2>
      {movie.Year}
      {movie.Genre}
      <h2>Language</h2>
      {movie.Language}
      <h2>Runtime</h2>
      {movie.Runtime}
      <h2>Actors</h2>
      {movie.Actors}
      <h2>Ratings</h2>
      {movie.Ratings &&
        movie.Ratings.map((rating) => {
          return (
            <section key={movie.imdbID + movie.Ratings.indexOf(rating)}>
              <h3>{rating.Source}</h3>
              {rating.Value}
            </section>
          );
        })}
    </>
  );
};
