    .get(`https://www.omdbapi.com/?apikey=20dac387&s=${buscador}&type=${type}`)
    .then((response) => response.data)
    .then((data) => data.Search)
    .then((movies) => {
      dispatch(setMovies(movies));
      return movies;
    });
};

export default moviesReducer;
