import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";

import axios from "axios";

import { useSelector, useDispatch } from "react-redux";
import { setMovies } from "../store/movies";
import { setUser } from "../store/user";

export const Movies = () => {
  const movies = useSelector((state) => state.movies);
  const user = useSelector((state) => state.user);

  const dispatch = useDispatch();

  const [buscador, setBuscador] = useState("");
  const [type, setType] = useState("movie");

  const [loading, setLoading] = useState(true);

  const history = useHistory();

  useEffect(() => {
    axios
      .get("/api/movies")
      .then((res) => res.data)
      .then((user) => {
        dispatch(setUser(user));
        setLoading(false);
      })
      .catch(() => {
        setLoading(false);
      });
  }, [dispatch]);

  useEffect(() => {
    axios
      .get(
        `https://www.omdbapi.com/?apikey=20dac387&s=${buscador}&type=${type}"`
      )
      .then((response) => response.data)
      .then((data) => data.Search)
      .then((movies) => dispatch(setMovies(movies)));
  }, [buscador, type, dispatch]);

  const handleSearch = (e) => {
    const value = e.target.value;
    const input = e.target.name;
    if (input === "title") {
      return setBuscador(value);
    } else if (input === "type") {
      return setType(value);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const handleLogout = () => {
    axios
      .post(
        "/api/movies/logout",
        dispatch(setUser("")) // setConfirm("1"));
      )
      .then(() => history.push("/"));
  };

  if (loading) return null;

  {
    user ? console.log(user.email) : console.log("user is undefined");
  }
  if (user) {
    return (
      <>
        <header>
          <nav>
            <button onClick={handleLogout}>LOG OUT</button>;
          </nav>
        </header>

        {movies &&
          movies.map((movie) => {
            return (
              <section key={movie.imdbID}>
                <Link to={`movie/${movie.Title}`}>
                  <h1>{movie.Title}</h1>
                  <img src={movie && movie.Poster} alt={movie.Title} />
                  <h3>{movie.Year}</h3>
                </Link>
              </section>
            );
          })}
      </>
    );
  }
  return <>HAS INGRESADO UN USUARIO NO V√ÅLIDO</>;
};
