import React, { useState } from "react";
import axios from "axios";
import { useHistory, Link } from "react-router-dom";

import { useSelector, useDispatch } from "react-redux";
import { setEmail } from "../store/email";
import { setPassword } from "../store/password";
import { setUser } from "../store/user";

import { Navbar } from "../components/NavBar";

import {
  Button,
  Typography,
  Grid,
  FormControl,
  Input,
  InputLabel,
} from "@material-ui/core";

export const Login = () => {
  const email = useSelector((state) => state.email);
  const password = useSelector((state) => state.password);
  const [loading, setLoading] = useState(false);

  const dispatch = useDispatch();
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      axios
        .post("/api/login", {
          email,
          password,
        })
        .then(({ data }) => {
          dispatch(setUser(data));
          history.push("/movies");
          setLoading(false);
        })
        .catch(() => {
          alert("Usuario o contraseña incorrecto");
          setLoading(false);
        });
    } catch {
      console.log("OCURRIÓ UN ERROR");
    }
  };

  const handleChange = (e) => {
    const type = e.target.type;
    const value = e.target.value;
    if (type === "email") {
      dispatch(setEmail(value));
    } else if (type === "password") {
      dispatch(setPassword(value));
    }
  };

  if (loading)
    return (
      <div className="i">
        <img src="esperarCarga.jpg" alt="Loading" />
      </div>
    );

  return (
    <Grid container>
      <Grid item md={12}>
        <FormControl onSumbit={handleSubmit}>
          <InputLabel htmlFor="email">Email </InputLabel>
          <Input id="email" type="email"></Input>
        </FormControl>
      </Grid>
      <Grid item md={12}>
        <FormControl>
          <InputLabel htmlFor="pwd">Email </InputLabel>
          <Input id="pwd" type="pwd"></Input>
        </FormControl>
      </Grid>
      <Button variant="contained" color="secundary" disableElevation>
        LOG IN
      </Button>
    </Grid>
  );
};
