import React from "react";
import axios from "axios";
import { useHistory, Link } from "react-router-dom";

import { useSelector, useDispatch } from "react-redux";
import { setEmail } from "../store/email";
import { setPassword } from "../store/password";

import { Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles({
  buttons: {
    background: "linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)",
    border: 0,
    borderRadius: 3,
    boxShadow: "0 3px 5px 2px rgba(255, 105, 135, .3)",
    color: "white",
    height: 48,
    padding: "0 30px",
  },
});

export const Register = () => {
  const email = useSelector((state) => state.email);

  const password = useSelector((state) => state.password);

  const dispatch = useDispatch();
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .post("/api/register", {
        email,
        password,
      })
      .then(() => alert("Te registraste con éxito"));
    history.push("/login");
  };

  const handleChange = (e) => {
    const type = e.target.type;
    const value = e.target.value;
    if (type === "email") {
      dispatch(setEmail(value));
    } else if (type === "password") {
      dispatch(setPassword(value));
    }
  };

  const classes = useStyles();

  return (
    <>
      <Link to="/login">
        <Button className={classes.buttons} onClick={handleFavs}>
          I HAVE ACCOUNT
        </Button>
      </Link>
      <form onSubmit={handleSubmit}>
        <input
          placeholder="ingrese su email"
          onChange={handleChange}
          type="email"
          value={email}
        />
        <br />
        <br />
        <input
          placeholder="ingrese su contraseña"
          onChange={handleChange}
          type="password"
        />

        <Button className={classes.buttons}>SING IN</Button>
      </form>
    </>
  );
};
