import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getManyByTitle } from "../hooks/hooks";
import { Movie } from "./Movie";

export const Movies = () => {
  const [movies, setMovies] = useState([]);
  const [buscador, setBuscador] = useState("");

  useEffect(() => {
    getManyByTitle({ buscador }).then((movies) => setMovies(movies));
  }, []);

  const handleSearch = (e) => {
    const value = e.target.value;
    return setBuscador(value);
  };

  return (
    <>
      <input type="text" value={buscador} onChange={handleSearch} />
      {movies &&
        movies.map((movie) => {
          return (
            <section key={movie.imdbID}>
              <Link to="/movie/name">
                <h1>{movie.Title}</h1>
                <img src={movie && movie.Poster} />
                <h3>{movie.Year}</h3>
              </Link>
            </section>
          );
        })}
    </>
  );
};
