import React, { useState, useEffect } from "react";
import { getOneByTitle } from "../hooks/hooks";

export const Movie = (props) => {
  const [movie, setMovie] = useState(props.match.params.title);

  /*  useEffect(() => {
    getOneByTitle(props.match.params.title).then((data) => setMovie(data));
  }, []); */

  return (
    <>
      {console.log(props.match.params)}
      <nav></nav>
      <h1>{movie.Title}</h1>
      <br />
      <img src={movie.Poster} alt={movie.Title} />
      <br />
      <h2>Description</h2>
      {movie.Year}
      {movie.Genre}
      {movie.Lenguage}
      {movie.Runtime}
      {movie.Actors}
      {movie.Ratings &&
        movie.Ratings.map((ratings) => {
          return (
            <section key={movie.imdbID}>
              <h3>{ratings.Source}</h3>
              {ratings.Value}
            </section>
          );
        })}
    </>
  );
};
