import { createAction, createReducer } from "@reduxjs/toolkit";
import axios from "axios";

export const setMovies = createAction("getMovies");

export const getOneByTitle = createAction("getMovie");

const moviesReducer = createReducer([], {
  [getManyByTitleAndType]: (state, title) => {
    axios
      .get(`https://www.omdbapi.com/?apikey=20dac387&s=${title}&type=movie"`)

      .then((response) => response.data)
      .then((data) => data.Search);
  },

  [getOneByTitle]: (state) => state - 1,
});

export default moviesReducer;
