import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";

import axios from "axios";

import { useSelector, useDispatch } from "react-redux";
import { setMovies } from "../store/movies";
import { setUser } from "../store/user";

export const Movies = () => {
  const num = 2;

  const movies = useSelector((state) => state.movies);
  const user = useSelector((state) => state.user);

  const dispatch = useDispatch();

  const [buscador, setBuscador] = useState("");
  const [type, setType] = useState("movie");

  const ruta = `https://www.omdbapi.com/?apikey=20dac387&s=${buscador}&type=${type}&page=${num}`;

  const [loading, setLoading] = useState(true);

  const history = useHistory();

  useEffect(() => {
    axios
      .get("/api/movies")
      .then((res) => res.data)
      .then((user) => {
        dispatch(setUser(user));
        setLoading(false);
      })
      .catch(() => {
        setLoading(false);
      });
  }, [dispatch]);

  useEffect(() => {
    axios
      .get(ruta)
      .then((response) => response.data)
      .then((data) => data.Search)
      .then((movies) => dispatch(setMovies(movies)));
  }, [buscador, type, dispatch]);

  const handleSearch = (e) => {
    const value = e.target.value;
    const input = e.target.name;
    if (input === "title") {
      return setBuscador(value);
    } else if (input === "type") {
      return setType(value);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const handleLogout = () => {
    axios
      .post(
        "/api/movies/logout",
        dispatch(setUser("")) // setConfirm("1"));
      )
      .then(() => history.push("/"));
  };

  const handlePage = () => {
    axios
      .get(
        `https://www.omdbapi.com/?apikey=20dac387&s=${buscador}&type=${type}&page=${num}`
      )
      .then((response) => response.data)
      .then((data) => data.Search)
      .then((movies) => dispatch(setMovies(movies)));
    num = num + 1;
  };

  if (loading) return null;

  {
    user ? console.log(user.email) : console.log("user is undefined");
  }
  if (user) {
    return (
      <>
        <header>
          <nav>
            <button onClick={handleLogout}>LOG OUT</button>
          </nav>
        </header>

        <form onSubmit={handleSubmit}>
          <h3>Title</h3>
          <input
            placeholder="ingrese el título que busca"
            type="text"
            value={buscador}
            onChange={handleSearch}
            name="title"
          />
          <h3>Type</h3>
          <input type="text" value={type} onChange={handleSearch} name="type" />
        </form>
        <div className="gridContainer">
          {movies &&
            movies.map((movie) => {
              return (
                <div className="gridItem" key={movie.imdbID}>
                  <section>
                    <Link to={`movie/${movie.Title}`}>
                      <h2>{movie.Title}</h2>
                      <img src={movie && movie.Poster} alt={movie.Title} />
                      <h3>{movie.Year}</h3>
                    </Link>
                  </section>
                </div>
              );
            })}
          <button onClick={handlePage}>NEXT</button>
        </div>
      </>
    );
  }
  return <>HAS INGRESADO UN USUARIO NO VÁLIDO</>;
};
