import React, { useEffect, useState } from "react";

import { useHistory } from "react-router-dom";

import { useSelector, useDispatch } from "react-redux";
import axios from "axios";

import { setMovies } from "../store/movies";

import { setUser } from "../store/user";

import { setFavs } from "../store/favs";

export const Movie = (props) => {
  const param = props.match.params.title;

  const user = useSelector((state) => state.user);
  const movie = useSelector((state) => state.movies);
  /* const favs = useSelector((state) => state.favorites);
   */

  const history = useHistory();

  /*   const [favs, setFavs] = useState([]); */

  const dispatch = useDispatch();

  useEffect(() => {
    axios
      .get(`https://www.omdbapi.com/?apikey=20dac387&t=${param}"`)

      .then((response) => response.data)
      .then((data) => data)
      .then((data) => dispatch(setMovies(data)));
  }, [param, dispatch]);

  useEffect(() => {
    axios
      .get("/api/movies")
      .then((res) => res.data)
      .then((user) => {
        dispatch(setUser(user));
      })
      .catch(() => {});
  }, [dispatch]);

  const handelFavs = () => {
    setFavs([...favs, movie]);
  };

  if (user) {
    return (
      <>
        <h3>LOS FAVORITOOOOOS {favs && console.log(favs)}</h3>
        <nav>
          <button onClick={handelFavs}>Agregar a Favoritos</button>

          <br />
          <br />

          <button onClick={() => history.push("/movies")}>TO BACK</button>
        </nav>
        <h2>{movie.Title}</h2>
        <br />
        <img src={movie.Poster} alt={movie.Title} />
        <br />
        <h2>Description</h2>
        {movie.Plot}
        <h2>Year and Genre</h2>
        {movie.Year}
        {movie.Genre}
        <h2>Language</h2>
        {movie.Language}
        <h2>Runtime</h2>
        {movie.Runtime}
        <h2>Actors</h2>
        {movie.Actors}
        <h2>Ratings</h2>
        {movie.Ratings &&
          movie.Ratings.map((rating) => {
            return (
              <section key={movie.imdbID + movie.Ratings.indexOf(rating)}>
                <h3>{rating.Source}</h3>
                {rating.Value}
              </section>
            );
          })}
      </>
    );
  }
};
