import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import axios from "axios";
import { setMovies } from "../store/movies";
import { Favorites } from "./Favorites";

import { setFavs } from "../store/favs";

export const Movie = (props) => {
  const param = props.match.params.title;
  const movie = useSelector((state) => state.movies);
  /* const favs = useSelector((state) => state.favorites);
   */

  const history = useHistory();

  const [favs, setFavs] = useState([]);

  const dispatch = useDispatch();

  useEffect(() => {
    axios
      .get(`https://www.omdbapi.com/?apikey=20dac387&t=${param}"`)

      .then((response) => response.data)
      .then((data) => data)
      .then((data) => dispatch(setMovies(data)));
  }, [param, dispatch]);

  useEffect(() => {
    axios
      .get(`api/favorites`)

      .then((response) => response.data)
      .then((data) => data)
      .then((data) => dispatch(setFavs(data)));
  }, [favs]);

  const handelFavs = () => {
    axios.post("api/favorites", { movie });
  };

  return (
    <>
      <ul>
        <ol>
          <button onClick={() => history.push("/movies")}>TO BACK</button>
        </ol>
        <ol>
          <button onClick={handelFavs}>Agregar a Favoritos</button>
        </ol>
      </ul>

      <div className="favs">
        <Favorites />
      </div>

      <ul>
        {favs &&
          favs.map((fav) => {
            return <li>{fav}</li>;
          })}
      </ul>

      <h2>{movie.Title}</h2>
      <div className="gridItem">
        <div>
          <img src={movie.Poster} alt={movie.Title} />
        </div>

        <div>
          <h2>Description</h2>

          {movie.Plot}
        </div>
        <h2>Year and Genre</h2>
        <h4>{movie.Year}</h4>
        <h4>{movie.Genre}</h4>
        <h2>Language</h2>
        {movie.Language}
        <h2>Runtime</h2>
        {movie.Runtime}
        <h2>Actors</h2>
        {movie.Actors}
        <h2>Ratings</h2>
        {movie.Ratings &&
          movie.Ratings.map((rating) => {
            return (
              <section key={movie.imdbID + movie.Ratings.indexOf(rating)}>
                <h3>{rating.Source}</h3>
                {rating.Value}
              </section>
            );
          })}
      </div>
    </>
  );
};
