import React, { useEffect, useState } from "react";
import axios from "axios";

export default Secret = () => {
  const [image, setImage] = useState("");

  useEffect(() => {
    axios
      .get("/api/secret")
      .then((res) => res.data)
      .then((img) => {
        setImage(img);
      });
  }, []);

  return (
    <div>
      <img src={`/${image}`} alt="the cake is a lie" width="400" />
    </div>
  );
};
