import React, { useEffect } from "react";
import axios from "axios";

import { useSelector, useDispatch } from "react-redux";
import { setFavs } from "../store/favs";

import { Button, Grid, Typography, Box } from "@material-ui/core";

export const Favorites = () => {
  const dispatch = useDispatch();

  const favs = useSelector((state) => state.favorites);
  const user = useSelector((state) => state.user);

  useEffect(() => {
    axios
      .get(`/api/favorites/${user.id}`)
      .then(({ data }) => dispatch(setFavs(data)));
  }, []);

  const handleFavs = (id) => {
    return axios.delete(`/api/favorites/${id}`);
  };

  return (
    <>
      <Box
        border={3}
        borderColor="secondary.main"
        borderRadius={8}
        bgcolor="teal"
        alignSelf
      >
        <Typography variant="h4" color="secondary" align="center">
          FAVORITOS:
        </Typography>
      </Box>
      <Grid
        container
        style={{ padding: 10 }}
        spacing={2}
        direction="row"
        justify="space-evenly"
        alignItems="flex-start"
      >
        {favs &&
          favs.map((fav) => {
            return (
              <Box border={3} borderColor="primary.main" borderRadius={16}>
                <Grid item nowrap style={{ padding: 10 }} xs md={4}>
                  <Typography variant="h3" color="primary">
                    {fav.title}
                  </Typography>
                  <img src={fav.poster} alt={fav.title} />
                </Grid>
                <Button
                  color="secondary"
                  variant="contained"
                  disableElevation
                  onClick={() => handleFavs(fav.id)}
                  size="small"
                >
                  BORRAR
                </Button>
              </Box>
            );
          })}
      </Grid>
    </>
  );
};
