import axios from "axios";

export const getManyByTitle = (title) => {
  return axios
    .get(`https://www.omdbapi.com/?apikey=20dac387&s=${title}&y"`)

    .then((response) => response.data)
    .then((data) => data.Search);
};

export const getOneByTitle = (title) => {
  return axios
    .get(`https://www.omdbapi.com/?apikey=20dac387&t=${title}&y"`)

    .then((response) => response.data)
    .then((data) => data);
};
