import React, { useState, useEffect } from "react";
import { getOneByTitle } from "../hooks/hooks";

export const Movie = (props) => {
  const param = props.match.params.title;
  const [movie, setMovie] = useState({});

  useEffect(() => {
    getOneByTitle(param).then((data) => setMovie(data));
  }, []);

  return (
    <>
      <nav></nav>
      <h1>{movie.Title}</h1>
      <br />
      <img src={movie.Poster} alt={movie.Title} />
      <br />
      <h2>Description</h2>
      {movie.Plot}
      <h2>Year and Genre</h2>
      {movie.Year}
      {movie.Genre}
      <h2>Language</h2>
      {movie.Language}
      <h2>Runtime</h2>
      {movie.Runtime}
      <h2>Actors</h2>
      {movie.Actors}
      <section>
        <h2>Ratings</h2>
        <h3>{movie.Ratings[0].Source}</h3>
        <h3>{movie.Ratings[0].value}</h3>
        <h3>{movie.Ratings[1].Source}</h3>
        <h3>{movie.Ratings[1].value}</h3>
        <h3>{movie.Ratings[2].Source}</h3>
        <h3>{movie.Ratings[2].value}</h3>
      </section>
    </>
  );
};
