import React, { useState, useEffect } from "react";
import { getOneByTitle } from "../hooks/hooks";

export const Movie = (props) => {
  const param = props.match.params.title;
  const [movie, setMovie] = useState({});

  useEffect(() => {
    getOneByTitle(param).then((data) => setMovie(data));
  }, []);

  return (
    <>
      <nav></nav>
      <h1>{movie.Title}</h1>
      <br />
      <img src={movie.Poster} alt={movie.Title} />
      <br />
      <h2>Description</h2>
      {movie.Plot}
      <h2>Year and Genre</h2>
      {movie.Year}
      {movie.Genre}
      <h2>Language</h2>
      {movie.Language}
      <h2>Runtime</h2>
      {movie.Runtime}
      <h2>Actors</h2>
      {movie.Actors}
      <h2>Ratings</h2>
      {movie.Ratings &&
        movie.Ratings.map((ratings) => {
          return (
            <section key={movie.imdbID}>
              <h3>{ratings.Source}</h3>
              {ratings.Value}
            </section>
          );
        })}
    </>
  );
};
