import axios from "axios";

export const getManyByTitleAndType = (title) => {
  return axios
    .get(`https://www.omdbapi.com/?apikey=20dac387&s=${title}"`)

    .then((response) => response.data)
    .then((data) => data.Search);
};

export const getOneByTitle = (title) => {
  return axios
    .get(`https://www.omdbapi.com/?apikey=20dac387&t=${title}"`)

    .then((response) => response.data)
    .then((data) => data);
};

export const getManyByTitleSeries = ({ title, type }) => {
  return axios
    .get(`https://www.omdbapi.com/?apikey=20dac387&s=${title}&type=${type}"`)

    .then((response) => response.data)
    .then((data) => data.Search);
};
