import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getManyByTitleAndType } from "../hooks/hooks";
import axios from "axios";
import { Logout } from "./Logout";

export const Movies = () => {
  const [movies, setMovies] = useState([]);
  const [buscador, setBuscador] = useState("");
  const [type, setType] = useState("movie");
  const [confirm, setConfirm] = useState("2");

  useEffect(() => {
    axios
      .get("/api/movies")
      .then((res) => res.data)
      .then((conf) => {
        console.log(conf);
        setConfirm(conf);
      })
      .catch(() => {});
  }, [confirm]);

  useEffect(() => {
    getManyByTitleAndType(buscador, type).then((movies) => setMovies(movies));
  }, [buscador, type]);

  const handleSearch = (e) => {
    const value = e.target.value;
    const input = e.target.name;
    if (input === "title") {
      return setBuscador(value);
    } else if (input === "type") {
      return setType(value);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  if (confirm === "2") {
    return <>HAS INGRESADO UN USUARIO NO VÁLIDO</>;
  } else {
    return (
      <>
        {/*   <Link to="/login">
        <button>INGRESAR</button>
      </Link>

      <Link to="/register">
        <button>REGRISTRARSE</button>
      </Link> */}
        <Logout />

        <form onSubmit={handleSubmit}>
          <h3>Title</h3>
          <input
            placeholder="ingrese el título que busca"
            type="text"
            value={buscador}
            onChange={handleSearch}
            name="title"
          />
          <h3>Type</h3>
          <input type="text" value={type} onChange={handleSearch} name="type" />
        </form>
        {movies &&
          movies.map((movie) => {
            return (
              <section key={movie.imdbID}>
                <Link to={`movie/${movie.Title}`}>
                  <h1>{movie.Title}</h1>
                  <img src={movie && movie.Poster} alt={movie.Title} />
                  <h3>{movie.Year}</h3>
                </Link>
              </section>
            );
          })}
      </>
    );
  }
};
