import React, { useState, useEffect } from "react";
import { getOneByTitle } from "../hooks/hooks";

export const Movie = () => {
  const [movie, setMovie] = useState({});

  useEffect(() => {
    getOneByTitle("batman").then((data) => setMovie(data));
  }, []);

  return (
    <>
      <nav>
        <input type="text" />
      </nav>
      <h1>{movie.Title}</h1>
      <br />
      <img src={movie.Poster} alt={movie.Title} />
      <br />
      <h2>Description</h2>
      {movie.Year}
      {movie.Genre}
      {movie.Lenguage}
      {movie.Runtime}
      {movie.Actors}
      {movie.Ratings &&
        movie.Ratings.map((ratings) => {
          return (
            <section key={movie.imdbID}>
              <h3>{ratings.Source}</h3>
              {ratings.Value}
            </section>
          );
        })}
    </>
  );
};
