import React, { useEffect } from "react";
import { useHistory } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";

import axios from "axios";

import { setMovie } from "../store/singleMovie";

import { Button } from "@material-ui/core";

export const SingleMovie = (props) => {
  const param = props.match.params.title;
  const movie = useSelector((state) => state.singleMovie);

  const user = useSelector((state) => state.user);
  console.log(movie.imdbID);
  const history = useHistory();

  const dispatch = useDispatch();

  useEffect(() => {
    axios
      .get(`https://www.omdbapi.com/?apikey=20dac387&t=${param}"`)

      .then((response) => response.data)
      .then((data) => data)
      .then((data) => dispatch(setMovie(data)));
  }, []);

  const handleFavs = () => {
    axios.post("/api/favorites", {
      title: movie.Title,
      year: movie.Year,
      poster: movie.Poster,
      favId: user.id,
    });
  };

  return (
    <>
      <ul>
        <ol>
          <Button
            color="secondary"
            variant="contained"
            disableElevation
            onClick={handleFavs}
          >
            ADD TO FAVS
          </Button>
        </ol>
        <ol>
          <Button
            color="secondary"
            variant="contained"
            disableElevation
            onClick={() => history.push("/favorites")}
          >
            FAVORITES
          </Button>
        </ol>
      </ul>

      <h2>{movie.Title}</h2>
      <div className="gridItem">
        <div>
          <img src={movie.Poster} alt={movie.Title} />
        </div>

        <div>
          <h2>Description</h2>

          {movie.Plot}
        </div>
        <h2>Year and Genre</h2>
        <h4>{movie.Year}</h4>
        <h4>{movie.Genre}</h4>
        <h2>Language</h2>
        {movie.Language}
        <h2>Runtime</h2>
        {movie.Runtime}
        <h2>Actors</h2>
        {movie.Actors}
        <h2>Ratings</h2>
        {movie.Ratings &&
          movie.Ratings.map((rating) => {
            return (
              <section key={movie.imdbID + movie.Ratings.indexOf(rating)}>
                <h3>{rating.Source}</h3>
                {rating.Value}
              </section>
            );
          })}
      </div>
    </>
  );
};
