import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getManyByTitleAndType } from "../hooks/hooks";
import { Movie } from "./Movie";

export const Movies = () => {
  const [movies, setMovies] = useState([]);
  const [buscador, setBuscador] = useState("");
  const [type, setType] = useState("movie");

  useEffect(() => {
    getManyByTitleAndType(buscador, type).then((movies) => setMovies(movies));
  }, [buscador, type]);

  const handleSearch = (e) => {
    const value = e.target.value;
    const input = e.target.name;
    if (input === "title") {
      return setBuscador(value);
    } else if (input === "type") {
      return setType(value);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const getMovie = (val) => {
    <Movie movie={val} />;
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <h3>Title</h3>
        <input
          placeholder="ingrese el tÃ­tulo "
          type="text"
          value={buscador}
          onChange={handleSearch}
          name="title"
        />
        <h3>Type</h3>
        <input
          placeholder=""
          type="text"
          value={type}
          onChange={handleSearch}
          name="type"
        />
      </form>
      {movies &&
        movies.map((movie) => {
          return (
            <section key={movie.imdbID}>
              <h1 onClick={getMovie}>{movie.Title}</h1>
              <img src={movie && movie.Poster} />
              <h3>{movie.Year}</h3>
            </section>
          );
        })}
    </>
  );
};
