import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import { setMovies } from "../store/movies";
import { setUser } from "../store/user";

export const Movies = () => {
  const movies = useSelector((state) => state.movies);
  const user = useSelector((state) => state.user);
  //////////////////////
  const [num, setNum] = useState(2);
  const [buscador, setBuscador] = useState("");
  const [type, setType] = useState("movie");
  ////////////////////////
  const dispatch = useDispatch();
  const history = useHistory();

  useEffect(() => {
    if (movies.length > 0) {
      return axios
        .get(
          `https://www.omdbapi.com/?apikey=20dac387&s=${buscador}&type=${type}`
        )
        .then((response) => response.data)
        .then((data) => data.Search)
        .then((movies) => dispatch(setMovies(movies)));
    } else
      return axios
        .get(`https://www.omdbapi.com/?apikey=20dac387&s=batman`)
        .then((response) => response.data)
        .then((data) => data.Search)
        .then((movies) => dispatch(setMovies(movies)));
  }, [buscador, type]);

  ///////////////////////7
  // funciones

  const handleSearch = (e) => {
    const value = e.target.value;
    const input = e.target.name;
    if (input === "title") {
      return setBuscador(value);
    } else if (input === "type") {
      return setType(value);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const handleLogout = () => {
    axios
      .post("/api/movies/logout", dispatch(setUser("")))
      .then(() => history.push("/"));
  };

  const handleNextPage = () => {
    axios
      .get(
        `https://www.omdbapi.com/?apikey=20dac387&s=${buscador}&type=${type}&page=${num}`
      )
      .then((response) => response.data)
      .then((data) => data.Search)
      .then((movies) => dispatch(setMovies(movies)));
    setNum(num + 1);
  };

  const handleBackPage = () => {
    axios
      .get(
        `https://www.omdbapi.com/?apikey=20dac387&s=${buscador}&type=${type}&page=${num}`
      )
      .then((response) => response.data)
      .then((data) => data.Search)
      .then((movies) => dispatch(setMovies(movies)));
    setNum(num - 1);
  };

  if (user && movies) {
    return (
      <>
        <header>
          <Button onClick={handleLogout}>LOG OUT</Button>
        </header>

        <form onSubmit={handleSubmit}>
          <ul>
            <li>
              <h3>Title</h3>
              <input
                placeholder="ingrese el título que busca"
                type="text"
                value={buscador}
                onChange={handleSearch}
                name="title"
              />
            </li>
            <li>
              <h3>Type</h3>

              <input
                type="text"
                value={type}
                onChange={handleSearch}
                name="type"
              />
            </li>
          </ul>
        </form>
        <div className="gridContainer">
          {movies &&
            movies.map((movie) => {
              return (
                <div
                  key={movie.imdbID}
                  onClick={() => history.push(`/movie/${movie.Title}`)}
                >
                  <section>
                    <h2>{movie.Title}</h2>
                  </section>
                  <section>
                    <img src={movie && movie.Poster} alt={movie.Title} />
                  </section>
                  <section>
                    <h3>{movie.Year}</h3>
                  </section>
                </div>
              );
            })}
        </div>
        <section>
          <Button
            color="secondary"
            variant="contained"
            onClick={handleBackPage}
          >
            BACK
          </Button>
          <Button
            color="secondary"
            variant="contained"
            onClick={handleNextPage}
          >
            NEXT
          </Button>
        </section>
      </>
    );
  }
  return <>HAS INGRESADO UN USUARIO NO VÁLIDO</>;
};
