import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Secret() {
  const [image, setImage] = useState(null);

  useEffect(() => {
    axios
      .get("/api/secret")
      .then((res) => res.data)
      .then((img) => {
        setImage(img);
      })
      .catch(() => {});
  }, [image]);

  if (image) {
    return (
      <div>
        <img src={`/${image}`} alt="the cake is a lie" width="400" />
        {setImage(null)}
      </div>
    );
  }

  return (
    <>
      <p>404 Not Found</p>
    </>
  );
}
