import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { getManyByTitleAndType } from "../hooks/hooks";
import axios from "axios";

import { useSelector, useDispatch } from "react-redux";
import { setMovies } from "../store/movies";

export const Movies = () => {
  const movies = useSelector((state) => state.movies);

  /* const [movies, setMovies] = useState([]); */
  const [buscador, setBuscador] = useState("");
  const [type, setType] = useState("movie");
  const [person, setPerson] = useState("");
  const [confirm, setConfirm] = useState(true);

  const history = useHistory();

  useEffect(() => {
    axios
      .get("/api/movies")
      .then((res) => res.data)
      .then((user) => {
        setPerson(user);
        setConfirm(false);
        console.log(user);
      })
      .catch(({ response }) => {
        setConfirm(false);
        return response.status, response.statusText;
      });
  }, []);

  useEffect(() => {
    axios
      .get(
        `https://www.omdbapi.com/?apikey=20dac387&s=${buscador}&type=${type}"`
      )
      .then((response) => response.data)
      .then((data) => data.Search)
      .then((movies) => dispatch(setMovies(movies)));
  }, [buscador, type]);

  const handleSearch = (e) => {
    const value = e.target.value;
    const input = e.target.name;
    if (input === "title") {
      return setBuscador(value);
    } else if (input === "type") {
      return setType(value);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const handleLogout = () => {
    axios.post(
      "/api/movies/logout",
      setPerson(),
      history.push("/") // setConfirm("1"));
    );
  };

  console.log(person);
  if (person) {
    return (
      <>
        {/*   <Link to="/login">
        <button>INGRESAR</button>
      </Link>

      <Link to="/register">
        <button>REGRISTRARSE</button>
      </Link> */}
        <button onClick={handleLogout}>LOG OUT</button>;
        <form onSubmit={handleSubmit}>
          <h3>Title</h3>
          <input
            placeholder="ingrese el título que busca"
            type="text"
            value={buscador}
            onChange={handleSearch}
            name="title"
          />
          <h3>Type</h3>
          <input type="text" value={type} onChange={handleSearch} name="type" />
        </form>
        {movies &&
          movies.map((movie) => {
            return (
              <section key={movie.imdbID}>
                <Link to={`movie/${movie.Title}`}>
                  <h1>{movie.Title}</h1>
                  <img src={movie && movie.Poster} alt={movie.Title} />
                  <h3>{movie.Year}</h3>
                </Link>
              </section>
            );
          })}
      </>
    );
  }
  return <>HAS INGRESADO UN USUARIO NO VÁLIDO</>;
};
