import {
  createAction,
  createReducer,
  createAsyncThunk,
} from "@reduxjs/toolkit";
import axios from "axios";

export const setMovies = createAction("setMovies");

export const getMovies = createAsyncThunk("GET_MOVIES", ({ title, type }) => {
  return axios
    .get(`https://www.omdbapi.com/?apikey=20dac387&s=${title}&type=${type}`)
    .then((response) => response.data)
    .then((data) => data.Search)
    .then((movies) => dispatch(setMovies(movies)));
});

/*const moviesReducer = createReducer([], {
   [setMovies]: (state, action) => (state = action.payload),
});

export default moviesReducer; */

const moviesReducer = createReducer([], {
  [getMovies]: (state, action) => (state = action.payload),
});

export default moviesReducer;
