import React, { useEffect } from "react";
import axios from "axios";

import { useSelector, useDispatch } from "react-redux";
import { setFavs } from "../store/favs";

import { Button, Grid, Typography, Box } from "@material-ui/core";
import DeleteIcon from "@material-ui/icons/Delete";

export const Favorites = () => {
  const dispatch = useDispatch();

  const favs = useSelector((state) => state.favorites);
  const user = useSelector((state) => state.user);

  useEffect(() => {
    axios
      .get(`/api/favorites/${user.id}`)
      .then(({ data }) => dispatch(setFavs(data)));
  }, []);

  const handleFavs = (id) => {
    return axios.delete(`/api/favorites/${id}`);
  };

  return (
    <>
      <Grid
        container
        style={{ padding: 20 }}
        spacing={2}
        direction="row"
        justify="space-evenly"
        alignItems="flex-start"
      >
        <Typography varian="h2" color="textSecondary">
          FAVORITOS:
        </Typography>
        {favs &&
          favs.map((fav) => {
            return (
              <Box border={3} borderColor="primary.main" borderRadius={16}>
                <Grid item nowrap style={{ padding: 10 }} xs md={4}>
                  <Typography variant="h3" color="primary">
                    {fav.title}
                  </Typography>
                  <Typography>
                    <img src={fav.poster} alt={fav.title} />
                  </Typography>
                </Grid>

                <Button
                  color="secondary"
                  variant="contained"
                  disableElevation
                  onClick={() => handleFavs(fav.id)}
                  size="small"
                  startIcon={<DeleteIcon />}
                >
                  BORRAR
                </Button>
              </Box>
            );
          })}
      </Grid>
    </>
  );
};
