import React, { useState } from "react";
import axios from "axios";
import { useHistory, Link } from "react-router-dom";
import { Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

import { useSelector, useDispatch } from "react-redux";
import { setEmail } from "../store/email";
import { setPassword } from "../store/password";
import { setUser } from "../store/user";

///////////////////////M-UI
const useStyles = makeStyles({
  buttons: {
    background: "linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)",
    border: 0,
    borderRadius: 3,
    boxShadow: "0 3px 5px 2px rgba(255, 105, 135, .3)",
    color: "white",
    height: 48,
    padding: "0 30px",
  },
});

/////////////////////

export const Login = () => {
  const email = useSelector((state) => state.email);
  const password = useSelector((state) => state.password);
  const [loading, setLoading] = useState(false);

  const dispatch = useDispatch();
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      axios
        .post("/api/login", {
          email,
          password,
        })
        .then(({ data }) => {
          dispatch(setUser(data));
          history.push("/movies");
          setLoading(false);
        })
        .catch(() => {
          alert("Usuario o contraseña incorrecto");
          setLoading(false);
        });
    } catch {
      console.log("OCURRIÓ UN ERROR");
    }
  };

  const handleChange = (e) => {
    const type = e.target.type;
    const value = e.target.value;
    if (type === "email") {
      dispatch(setEmail(value));
    } else if (type === "password") {
      dispatch(setPassword(value));
    }
  };

  ////////////M-UI

  const classes = useStyles();

  /////////////
  if (loading)
    return (
      <div className="i">
        <img src="esperarCarga.jpg" alt="Loading" />
      </div>
    );

  return (
    <>
      <Link to="/register">
        <Button className={classes.buttons}>SING IN</Button>
      </Link>
      <form onSubmit={handleSubmit}>
        <input
          placeholder="ingrese su email"
          onChange={handleChange}
          type="email"
          value={email}
        />
        <br />
        <br />
        <input
          placeholder="ingrese su contraseña"
          onChange={handleChange}
          type="password"
        />

        <Button className={classes.buttons} disableElevation>
          LOG IN<button></button>
        </Button>
      </form>
    </>
  );
};
